function auto_flip(handles);
data=get(handles.chan_table,'Data');
%chan_labels
for i=1:size(data,1);
   chan_labels{i}=data{i,1};
end;
%
chan_labels2=chan_labels;
for i=1:length(chan_labels);
    st1=chan_labels{i};
    tp=str2num(st1(end));
    if isempty(tp);
    else
        if mod(tp,2)==1;
            %is odd
            st=st1(1:end-1);
            st2=[st num2str(tp+1)];
            disp([st1 ' ' st2]);
            %does st2 exist?
            a=find(strcmpi(chan_labels,st2));
            if isempty(a);
            else
                chan_labels2{i}=st2;
            end;
        else
            %is even
            st=st1(1:end-1);
            st2=[st num2str(tp-1)];
            disp([st1 ' ' st2]);
            %does st2 exist?
            a=find(strcmpi(chan_labels,st2));
            if isempty(a);
            else
                chan_labels2{i}=st2;
            end;
        end;
    end;
end;
%
for i=1:length(chan_labels2);
    data{i,2}=chan_labels2{i};
end;
set(handles.chan_table,'Data',data);












